var request = require("request");
var Q = require("q");
var util = require('./util');

function get(containerID){
    var deferred = Q.defer();
    request({
        url: util.getAPIUrl("/container"),
        qs: {
            id: containerID,
            apiKey: util.getAPIkey()
        },
        method: 'GET',
        json: true
    },function(err, res, body){
        if(!err){
            deferred.resolve(body);
        } else {
            deferred.reject(err);
        }
    });
    
    return deferred.promise;
}


(function($){
    $.get = get;
    $.create = "createFKT";
    $.update = "updateFKT";
    $.getAll = "getAllFKT";
    $.install = "installFKT";
    $.inventory = "inventoryFKT";
})(exports);