var url = require('url');
var apiKey;
var baseURL = "https://api.server-eye.de/";
var apiVersion = 1;

function getAPIUrl(path) {
    var urlObj = url.parse(baseURL + apiVersion + path);
    return urlObj;
};

(function($){
    $.getAPIUrl = getAPIUrl;
    $.setAPIkey = function(key){
        if(typeof key === "string" && key.length){
            
            apiKey = key;
            return apiKey;
        }else {
            return undefined;
        }
    };
    $.getAPIkey = function(){
        return apiKey;
    };
})(exports);